    cpu: string;
    ram: string;
    graphics: string;
    totalDisk: string;
  };

  // Disk info
  diskInfo?: Array<{
    Device: string;
    Total: string;
    Used: string;
    Free: string;
    "Usage %": string;
  }>;

  // Process data
  topProcesses?: {
    top_cpu: Array<{
      pid: number;
      name: string;
      cpu_percent: number;
      memory_percent: number;
      memory_mb: number;
    }>;
    top_memory: Array<{
      pid: number;
      name: string;
      cpu_percent: number;
      memory_percent: number;
      memory_mb: number;
    }>;
