  // Network data
  networkInfo?: {
    local_ip: string;
    public_ip: string;
    location: string;
    nic_details: Array<{
      Description: string;
      MAC: string;
      ConnectionType: string;
      "IP Addresses": string;
    }>;
  };

  // Security data
  windowsSecurity?: {
    windows_defender: {
      antivirus_enabled: boolean;
      real_time_protection: boolean;
      am_service_running: boolean;
      last_quick_scan_days_ago: number;
    };
    firewall: Array<{
      profile: string;
      enabled: boolean;
    }>;
    uac_status: string;
    installed_av: Array<{
      name: string;
      state: string;
    }>;
    restart_pending: boolean;
    recent_patches: Array<{
      hotfix_id: string;
      installed_on: string;
    }>;
  };

  // Installed apps
  installedApps?: Array<{
    name: string;
    version: string;
    publisher: string;
    install_location: string;
  }>;

  // Open ports (optional)
  openPorts?: Array<{
    ip: string;
    local_port: number;
    process_name: string;
    protocol: string;
    service: string;
    critical: boolean;
    recommendation: string;
  }>;
}

interface DeviceDetailViewProps {
