              <div className="text-center pt-2">
                <Badge variant="secondary" className="text-xs">
                  +{criticalPorts.length - 5} more ports
                </Badge>
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/ProcessTable.tsx.content:
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";

interface Process {
  pid: number;
  name: string;
  cpu_percent: number;
  memory_percent: number;
  memory_mb: number;
}

interface ProcessTableProps {
  processes: Process[];
  title: string;
  type: "cpu" | "memory";
  systemUsage?: {
    cpu?: number;
    memory?: number;
    totalRam?: string;
  };
}

export default function ProcessTable({ 
  processes, 
  title, 
  type,
  systemUsage 
}: ProcessTableProps) {
  const formatBytes = (mb: number) => {
    if (mb < 1024) return `${mb.toFixed(1)} MB`;
    return `${(mb / 1024).toFixed(1)} GB`;
  };

  return (
    <Card data-testid={`card-process-${type}`}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-semibold">{title}</CardTitle>
          {systemUsage && (
            <div className="flex gap-4 text-sm">
              {systemUsage.cpu !== undefined && (
                <div className="flex items-center gap-2">
                  <span className="text-muted-foreground">System CPU:</span>
                  <Badge variant="outline" data-testid="text-system-cpu">
                    {systemUsage.cpu.toFixed(1)}%
                  </Badge>
                </div>
              )}
              {systemUsage.memory !== undefined && (
                <div className="flex items-center gap-2">
                  <span className="text-muted-foreground">System Memory:</span>
                  <Badge variant="outline" data-testid="text-system-memory">
                    {systemUsage.memory.toFixed(1)}%
                  </Badge>
                </div>
              )}
            </div>
          )}
        </div>
      </CardHeader>
      <CardContent>
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>PID</TableHead>
              <TableHead>Process Name</TableHead>
              <TableHead className="text-right">
                {type === "cpu" ? "CPU %" : "Memory"}
              </TableHead>
              <TableHead className="text-right">
                {type === "cpu" ? "Memory" : "Memory %"}
              </TableHead>
              <TableHead className="w-[100px]">Usage</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {processes.map((process, index) => (
              <TableRow key={`${process.pid}-${index}`} data-testid={`row-process-${process.pid}`}>
                <TableCell className="font-mono text-sm">
                  {process.pid}
                </TableCell>
                <TableCell className="font-medium max-w-[200px] truncate">
                  {process.name}
                </TableCell>
                <TableCell className="text-right font-mono">
                  {type === "cpu" 
                    ? `${process.cpu_percent.toFixed(1)}%`
                    : formatBytes(process.memory_mb)
                  }
                </TableCell>
                <TableCell className="text-right font-mono">
                  {type === "cpu"
                    ? formatBytes(process.memory_mb)
                    : `${process.memory_percent.toFixed(1)}%`
                  }
                </TableCell>
                <TableCell>
                  <Progress 
                    value={type === "cpu" ? process.cpu_percent : process.memory_percent}
                    className="h-2"
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </CardContent>
    </Card>
  );
}

client/src/components/SecurityOverview.t.content:
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import StatusIndicator from "./StatusIndicator";
import { Shield, ShieldCheck, ShieldAlert, AlertTriangle, Clock, CheckCircle } from "lucide-react";

interface WindowsDefenderStatus {
  antivirus_enabled: boolean;
  real_time_protection: boolean;
  am_service_running: boolean;
  last_quick_scan_days_ago: string | number;
}

interface FirewallProfile {
  profile: string;
  enabled: boolean;
}

interface AntivirusProduct {
  name: string;
  state: string;
}

interface SecurityOverviewProps {
  windowsDefender: WindowsDefenderStatus;
  firewall: FirewallProfile[];
  uacStatus: string;
  installedAv: AntivirusProduct[];
  restartPending: boolean;
  recentPatches: Array<{
    hotfix_id: string;
    installed_on: string;
  }>;
}

export default function SecurityOverview({
  windowsDefender,
  firewall,
  uacStatus,
  installedAv,
  restartPending,
  recentPatches
}: SecurityOverviewProps) {
  const getSecurityStatus = () => {
    if (!windowsDefender.antivirus_enabled || !windowsDefender.real_time_protection) {
      return "error";
    }
    if (restartPending || uacStatus === "Disabled") {
      return "warning";
    }
    return "online";
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6" data-testid="card-security-overview">
      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2">
            <Shield className="w-5 h-5" />
            Windows Defender
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="flex items-center justify-between">
            <span className="text-sm">Antivirus Protection</span>
            <StatusIndicator 
              status={windowsDefender.antivirus_enabled ? "online" : "error"}
              showLabel
              label={windowsDefender.antivirus_enabled ? "Enabled" : "Disabled"}
            />
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm">Real-time Protection</span>
            <StatusIndicator 
              status={windowsDefender.real_time_protection ? "online" : "error"}
              showLabel
              label={windowsDefender.real_time_protection ? "Enabled" : "Disabled"}
            />
          </div>
          <div className="flex items-center justify-between">
            <span className="text-sm">Service Status</span>
            <StatusIndicator 
              status={windowsDefender.am_service_running ? "online" : "error"}
              showLabel
              label={windowsDefender.am_service_running ? "Running" : "Stopped"}
            />
          </div>
          <div className="flex items-center justify-between pt-2 border-t border-card-border">
            <span className="text-sm">Last Scan</span>
            <Badge variant="outline" className="text-xs">
              {typeof windowsDefender.last_quick_scan_days_ago === 'number' 
                ? `${windowsDefender.last_quick_scan_days_ago} days ago`
                : windowsDefender.last_quick_scan_days_ago
              }
            </Badge>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2">
            <ShieldCheck className="w-5 h-5" />
            System Security
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="flex items-center justify-between">
            <span className="text-sm">User Account Control</span>
            <StatusIndicator 
              status={uacStatus === "Enabled" ? "online" : "warning"}
              showLabel
              label={uacStatus}
            />
          </div>

          {firewall.map((profile, index) => (
            <div key={index} className="flex items-center justify-between">
              <span className="text-sm">{profile.profile} Firewall</span>
              <StatusIndicator 
                status={profile.enabled ? "online" : "error"}
                showLabel
                label={profile.enabled ? "Enabled" : "Disabled"}
              />
            </div>
          ))}

          {restartPending && (
            <div className="flex items-center justify-between pt-2 border-t border-card-border">
              <div className="flex items-center gap-2">
                <AlertTriangle className="w-4 h-4 text-yellow-500" />
                <span className="text-sm">Restart Required</span>
              </div>
              <Badge variant="destructive">Pending</Badge>
            </div>
          )}
        </CardContent>
      </Card>

      <Card className="lg:col-span-2">
        <CardHeader className="pb-3">
          <CardTitle className="flex items-center gap-2">
            <Clock className="w-5 h-5" />
            Recent Security Updates
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {recentPatches.slice(0, 6).map((patch, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-muted rounded-md">
                <div className="flex items-center gap-2">
                  <CheckCircle className="w-4 h-4 text-green-500" />
                  <span className="font-mono text-sm" data-testid={`text-patch-${patch.hotfix_id}`}>
                    {patch.hotfix_id}
                  </span>
                </div>
                <span className="text-xs text-muted-foreground">
                  {new Date(patch.installed_on).toLocaleDateString()}
                </span>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {installedAv.length > 0 && (
        <Card className="lg:col-span-2">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2">
              <ShieldAlert className="w-5 h-5" />
              Third-party Antivirus
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {installedAv.map((av, index) => (
                <div key={index} className="flex items-center justify-between p-3 bg-muted rounded-md">
                  <span className="font-medium" data-testid={`text-av-${av.name}`}>{av.name}</span>
                  <StatusIndicator 
                    status={av.state === "Enabled" ? "online" : "error"}
                    showLabel
                    label={av.state}
                  />
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/StatusIndicator.ts.content:
import { cn } from "@/lib/utils";

interface StatusIndicatorProps {
  status: "online" | "offline" | "warning" | "error";
  size?: "sm" | "md" | "lg";
  showLabel?: boolean;
  label?: string;
  className?: string;
}

const statusConfig = {
  online: {
    color: "bg-green-500",
    label: "Online",
    pulse: true
  },
  offline: {
    color: "bg-gray-400",
    label: "Offline", 
    pulse: false
  },
  warning: {
    color: "bg-yellow-500",
    label: "Warning",
    pulse: true
  },
  error: {
    color: "bg-red-500", 
    label: "Error",
    pulse: true
  }
};

const sizeConfig = {
  sm: "w-2 h-2",
  md: "w-3 h-3",
  lg: "w-4 h-4"
};

export default function StatusIndicator({ 
  status, 
  size = "md", 
  showLabel = false, 
  label,
  className 
}: StatusIndicatorProps) {
  const config = statusConfig[status];
  const displayLabel = label || config.label;

  return (
    <div className={cn("flex items-center gap-2", className)} data-testid={`status-${status}`}>
      <div className="relative">
        <div className={cn(
          "rounded-full",
          config.color,
          sizeConfig[size]
        )} />
        {config.pulse && (
          <div className={cn(
            "absolute inset-0 rounded-full animate-ping",
            config.color,
            sizeConfig[size]
          )} />
        )}
      </div>
      {showLabel && (
        <span className="text-sm text-muted-foreground" data-testid={`text-${status}`}>
          {displayLabel}
        </span>
      )}
    </div>
  );
}

client/src/components/ThemeToggle.tsx.content:
import { Button } from "@/components/ui/button";
import { Moon, Sun } from "lucide-react";
import { useState, useEffect } from "react";

export default function ThemeToggle() {
  const [theme, setTheme] = useState<"light" | "dark">("dark");

  useEffect(() => {
    // Check for saved theme preference or default to 'dark' for monitoring
    const saved = localStorage.getItem("theme") as "light" | "dark" | null;
    const systemPreference = window.matchMedia("(prefers-color-scheme: dark)").matches ? "dark" : "light";
    const defaultTheme = saved || "dark"; // Default to dark for monitoring dashboard

    setTheme(defaultTheme);
    updateTheme(defaultTheme);
  }, []);

  const updateTheme = (newTheme: "light" | "dark") => {
    const root = document.documentElement;
    root.classList.remove("light", "dark");
    root.classList.add(newTheme);
  };

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    updateTheme(newTheme);
    localStorage.setItem("theme", newTheme);
    console.log(`Theme switched to ${newTheme}`);
  };

  return (
    <Button
      variant="ghost"
      size="icon"
      onClick={toggleTheme}
      className="hover-elevate"
      data-testid="button-theme-toggle"
      title={`Switch to ${theme === "light" ? "dark" : "light"} mode`}
    >
      {theme === "light" ? (
        <Moon className="h-5 w-5" />
      ) : (
        <Sun className="h-5 w-5" />
      )}
    </Button>
  );
}

